import { useRef, useEffect } from "react";


function App() {
  const tablero = useRef(null);
  useEffect(() => {
    buscaminas.numFilas = 10;
    buscaminas.numColumnas = 10;
    buscaminas.numMinasTotales = 10;
    const tablero = document.querySelector('#tablero');
    pintarTablero();
    generarCampoMinasVacio();
    esparcirMinas();
    contarMinas();
    actualizarcantMnas();
  }); 

  return (
    <>
    <script src="./components/BuscaMina.js"></script>
    <div className="App">
      <header className="App-header">
        <div id="tablero"></div> 
      	<div id="estado">
          <div>
            Cantidad de Minas: 
            <span id="cantMnas"></span>
          </div>
        </div>
      </header>
    </div>
    </>
  );
}

export default App;
