import React from 'react';
import './css/stylo.css';
import { useRef, useEffect } from "react";
import { pintarTablero, generarCampoMinasVacio, esparcirMinas, contarMinas, actualizarcantMnas } from "BuscaMina"
const buscaminas = {
  numMinasTotales: 0,
  numMinasEncontradas: 0,
  numFilas: 0,
  numColumnas: 0,
  aCampoMinas: []
}

export default function App() {
  const tablero = useRef(null);
  useEffect(() => {
    buscaminas.numFilas = 10;
    buscaminas.numColumnas = 10;
    buscaminas.numMinasTotales = 10;
    const tablero = document.querySelector('#tablero');
    pintarTablero();
    generarCampoMinasVacio();
    esparcirMinas();
    contarMinas();
    actualizarcantMnas();
  }); 

  return (
    <div className="App">
      <header className="App-header">
        <div id="tablero"></div> 
      	<div id="estado">
          <div>
            Cantidad de Minas: 
            <span id="cantMnas"></span>
          </div>
        </div>
      </header>
    </div>
  );
}

